import React, { useState } from "react";
import Cart from "./Cart"; // Імпорт компонента корзини

const ShopsPage = () => {
  const [selectedShop, setSelectedShop] = useState(null);
  const [cartItems, setCartItems] = useState([]);

  const handleShopSelect = (shop) => {
    setSelectedShop(shop);
  };

  const handleAddToCart = (product) => {
    const itemExists = cartItems.find((item) => item.id === product.id);

    if (itemExists) {
      // Якщо товар вже присутній у кошику, оновлюємо його кількість
      const updatedItems = cartItems.map((item) =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      );
      setCartItems(updatedItems);
    } else {
      // Якщо товару немає у кошику, додаємо його
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  return (
    <div>
      <h2>Сторінка магазинів</h2>
      <ul>
        {/* Відображення списку магазинів */}
        <li onClick={() => handleShopSelect("Магазин 1")}>Mac</li>
        <li onClick={() => handleShopSelect("Магазин 2")}>CFC</li>
        <li onClick={() => handleShopSelect("Магазин 3")}>Noa</li>
      </ul>
      {selectedShop && (
        <div>
          <p>
            Ви вибрали магазин: <strong>{selectedShop}</strong>
          </p>
          <h3>Продукти</h3>
          <ul>
            {/* Відображення списку продуктів */}
            <li onClick={() => handleAddToCart({ id: 1, name: "Продукт 1" })}>
              Продукт 1
            </li>
            <li onClick={() => handleAddToCart({ id: 2, name: "Продукт 2" })}>
              Продукт 2
            </li>
            <li onClick={() => handleAddToCart({ id: 3, name: "Продукт 3" })}>
              Продукт 3
            </li>
          </ul>
        </div>
      )}

      {/* Передача стану корзини та функції додавання товару до компонента корзини */}
      <Cart cartItems={cartItems} setCartItems={setCartItems} />
    </div>
  );
};

export default ShopsPage;
