import React, { useState } from "react";
import {
  Container,
  Title,
  ShopList,
  ShopItem,
  SelectedShop,
  ProductsTitle,
  List,
  ListItem,
  ProductName,
  AddToCartButton,
  ProductPrice,
} from "../styles/ShopsPage.styled";


const ShopsPage = ({ handleAddToCart }) => {
  const [selectedShop, setSelectedShop] = useState(null);

  const handleShopSelect = (shop) => {
    setSelectedShop(shop);
  };

  const renderProducts = () => {
    switch (selectedShop) {
      case "Mc Donny":
        return (
          <List>
            <ListItem>
              <ProductName>Burgers</ProductName>
              <ProductPrice>$10</ProductPrice>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart(
                    { id: 1, name: "Burgers", price: 10 },
                    selectedShop
                  )
                }
              >
                add to Card
              </AddToCartButton>
            </ListItem>
            <ListItem>
              <ProductName>Chicken & Fish Sandwiches</ProductName>
              <ProductPrice>$15</ProductPrice>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart(
                    { id: 2, name: "Chicken & Fish Sandwiches", price: 15 },
                    selectedShop
                  )
                }
              >
                add to Card
              </AddToCartButton>
            </ListItem>
          </List>
        );
      case "CFK":
        return (
          <List>
            <ListItem>
              <ProductName>Ice</ProductName>
              <ProductPrice>$5</ProductPrice>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart(
                    { id: 3, name: "Ice", price:"5" },
                    selectedShop
                  )
                }
              >
                add to Card
              </AddToCartButton>
            </ListItem>
            <ListItem>
              <ProductName>Dessert</ProductName>
              <ProductPrice>$15</ProductPrice>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart({ id: 4, name: "Dessert" }, selectedShop)
                }
              >
                add to Card
              </AddToCartButton>
            </ListItem>
          </List>
        );
      case "Noa":
        return (
          <List>
            <ListItem>
              <ProductName>Ramen with smoked chicken</ProductName>
              <ProductPrice>$18</ProductPrice>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart(
                    { id: 5, name: "Ramen with smoked chicken" },
                    selectedShop
                  )
                }
              >
                {" "}
                add to Card
              </AddToCartButton>
            </ListItem>
            <ListItem>
              <ProductName>Roll with salmon</ProductName>
              <ProductPrice>$20</ProductPrice>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart(
                    { id: 6, name: "Roll with salmon" },
                    selectedShop
                  )
                }
              >
                add to Card
              </AddToCartButton>
            </ListItem>
          </List>
        );
      default:
        return null;
    }
  };

  return (
    <Container>
      <Title>Shops Page</Title>
      <ShopList>
        <ShopItem onClick={() => handleShopSelect("Mc Donny")}>
          Mc Donny
        </ShopItem>
        <ShopItem onClick={() => handleShopSelect("CFK")}>CFK</ShopItem>
        <ShopItem onClick={() => handleShopSelect("Noa")}>Noa</ShopItem>
      </ShopList>
      {selectedShop && (
        <div>
          <SelectedShop>
            You have selected: <strong>{selectedShop}</strong>
          </SelectedShop>
          <ProductsTitle>Menu</ProductsTitle>
          {renderProducts()}
        </div>
      )}
    </Container>
  );
};

export default ShopsPage;
