import React, { useState } from "react";
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import "firebase/firestore";

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");

  const handleRemoveFromCart = (item) => {
    setCartItems((prevCartItems) =>
      prevCartItems.filter((cartItem) => cartItem.name !== item.name)
    );
  };

  const handleSubmitOrder = async () => {
    const orderData = {
      items: cartItems,
      email,
      phone,
      address,
    };

    try {
      const firestore = firebase.firestore();
      await firestore.collection("orders").add(orderData);
      alert("Замовлення надіслано");
      setCartItems([]);
      setEmail("");
      setPhone("");
      setAddress("");
    } catch (error) {
      console.error("Помилка надсилання замовлення:", error);
      alert("Помилка надсилання замовлення");
    }
  };

  return (
    <div>
      <h2>Кошик</h2>
      {cartItems.length === 0 ? (
        <p>Кошик порожній</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id}>
              {item.name} - {item.quantity}
              <button onClick={() => handleRemoveFromCart(item)}>
                Видалити
              </button>
            </li>
          ))}
        </ul>
      )}

      <h3>Дані замовлення</h3>
      <label>
        Електронна пошта:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </label>
      <label>
        Номер телефону:
        <input
          type="tel"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </label>
      <label>
        Адреса:
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
      </label>
      <button onClick={handleSubmitOrder}>Надіслати</button>
    </div>
  );
};

export default CartPage;
