import React, { useState } from "react";
import { Routes, Route, NavLink } from "react-router-dom";
import ShopsPage from "./components/ShopsPage";
import CartPage from "./components/CartPage";
import { Nav, Container, StyledNavLink } from "./styles/App.styled";

const App = () => {
  const [cartItems, setCartItems] = useState({});

  const handleAddToCart = (product, shop) => {
    setCartItems((prevCartItems) => {
      const updatedItems = { ...prevCartItems };
      if (updatedItems[shop]) {
        const itemExists = updatedItems[shop].find(
          (item) => item.id === product.id
        );
        if (itemExists) {
          itemExists.quantity += 1;
        } else {
          updatedItems[shop].push({ ...product, quantity: 1 });
        }
      } else {
        updatedItems[shop] = [{ ...product, quantity: 1 }];
      }
      return updatedItems;
    });
  };

  return (
    <Container>
      <Nav>
        <StyledNavLink to="/" end>
          ShopsPage
        </StyledNavLink>
        <NavLink to="/cart">Cart</NavLink>
      </Nav>
      <Routes>
        <Route
          path="/"
          element={<ShopsPage handleAddToCart={handleAddToCart} />}
        />
        <Route
          path="/cart"
          element={
            <CartPage cartItems={cartItems} setCartItems={setCartItems} />
          }
        />
      </Routes>
    </Container>
  );
};

export default App;
