import React, { useState } from "react";
import {
  Container,
  Title,
  ShopList,
  ShopItem,
  SelectedShop,
  ProductsTitle,
} from "../styles/ShopsPage.styled";


const ShopsPage = ({ handleAddToCart }) => {
  const [selectedShop, setSelectedShop] = useState(null);

  const handleShopSelect = (shop) => {
    setSelectedShop(shop);
  };

  const renderProducts = () => {
    switch (selectedShop) {
      case "Mc Donny":
        return (
          <ul>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 1, name: "Burgers" }, selectedShop)
                }
              >
                Burgers
              </button>
            </li>
            <li>
              <p>Chicken & Fish Sandwiches</p>

              <button
                onClick={() =>
                  handleAddToCart(
                    { id: 2, name: "Chicken & Fish Sandwiches" },
                    selectedShop
                  )
                }
              >
                Chicken & Fish Sandwiches
              </button>
            </li>
          </ul>
        );
      case "CFK":
        return (
          <ul>
            <li>
              <p>Ice</p>

              <button
                onClick={() =>
                  handleAddToCart({ id: 3, name: "Ice" }, selectedShop)
                }
              >
                add to Card
              </button>
            </li>
            <li>
              <p>Dessert</p>
              <button
                onClick={() =>
                  handleAddToCart({ id: 4, name: "Dessert" }, selectedShop)
                }
              >
                add to Card
              </button>
            </li>
          </ul>
        );
      case "Noa":
        return (
          <ul>
            <li>
              <p>Ramen with smoked chicken</p>
              <button
                onClick={() =>
                  handleAddToCart(
                    { id: 5, name: "Ramen with smoked chicken" },
                    selectedShop
                  )
                }
              >
                {" "}
                add to Card
              </button>
            </li>
            <li>
              <p>Roll with salmon</p>

              <button
                onClick={() =>
                  handleAddToCart(
                    { id: 6, name: "Roll with salmon" },
                    selectedShop
                  )
                }
              >
                add to Card
              </button>
            </li>
          </ul>
        );
      default:
        return null;
    }
  };

  return (
    <Container>
      <Title>Shops Page</Title>
      <ShopList>
        <ShopItem onClick={() => handleShopSelect("Mc Donny")}>Mc Donny</ShopItem>
        <ShopItem onClick={() => handleShopSelect("CFK")}>CFK</ShopItem>
        <ShopItem onClick={() => handleShopSelect("Noa")}>Noa</ShopItem>
      </ShopList>
      {selectedShop && (
        <div>
          <pSelectedShop>
            You have selected: <strong>{selectedShop}</strong>
          </pSelectedShop>
          <ProductsTitle>Продукти</ProductsTitle>
          {renderProducts()}
        </div>
      )}
    </Container>
  );
};

export default ShopsPage;
