import React, { useState } from "react";
import {
  Container,
  Title,
  ShopList,
  ShopItem,
  SelectedShop,
  ProductsTitle,
  ContainerProduct,
  List,
  ListItem,
  ProductName,
  AddToCartButton,
} from "../styles/ShopsPage.styled";


const ShopsPage = ({ handleAddToCart }) => {
  const [selectedShop, setSelectedShop] = useState(null);

  const handleShopSelect = (shop) => {
    setSelectedShop(shop);
  };

  const renderProducts = () => {
    switch (selectedShop) {
      case "Mc Donny":
        return (
          <List>
            <ListItem>
              <ProductName>Burgers</ProductName>

              <AddToCartButton
                onClick={() =>
                  handleAddToCart({ id: 1, name: "Burgers" }, selectedShop)
                }
              >
                add to Card
              </AddToCartButton>
            </ListItem>
            <ListItem>
              <p>Chicken & Fish Sandwiches</p>

              <button
                onClick={() =>
                  handleAddToCart(
                    { id: 2, name: "Chicken & Fish Sandwiches" },
                    selectedShop
                  )
                }
              >
                add to Card
              </button>
            </ListItem>
          </List>
        );
      case "CFK":
        return (
          <ul>
            <ListItem>
              <p>Ice</p>

              <button
                onClick={() =>
                  handleAddToCart({ id: 3, name: "Ice" }, selectedShop)
                }
              >
                add to Card
              </button>
            </ListItem>
            <ListItem>
              <p>Dessert</p>
              <button
                onClick={() =>
                  handleAddToCart({ id: 4, name: "Dessert" }, selectedShop)
                }
              >
                add to Card
              </button>
            </ListItem>
          </ul>
        );
      case "Noa":
        return (
          <ul>
            <ListItem>
              <p>Ramen with smoked chicken</p>
              <button
                onClick={() =>
                  handleAddToCart(
                    { id: 5, name: "Ramen with smoked chicken" },
                    selectedShop
                  )
                }
              >
                {" "}
                add to Card
              </button>
            </ListItem>
            <li>
              <p>Roll with salmon</p>

              <button
                onClick={() =>
                  handleAddToCart(
                    { id: 6, name: "Roll with salmon" },
                    selectedShop
                  )
                }
              >
                add to Card
              </button>
            </li>
          </ul>
        );
      default:
        return null;
    }
  };

  return (
    <Container>
      <Title>Shops Page</Title>
      <ShopList>
        <ShopItem onClick={() => handleShopSelect("Mc Donny")}>
          Mc Donny
        </ShopItem>
        <ShopItem onClick={() => handleShopSelect("CFK")}>CFK</ShopItem>
        <ShopItem onClick={() => handleShopSelect("Noa")}>Noa</ShopItem>
      </ShopList>
      {selectedShop && (
        <div>
          <SelectedShop>
            You have selected: <strong>{selectedShop}</strong>
          </SelectedShop>
          <ProductsTitle>Menu</ProductsTitle>
          {renderProducts()}
        </div>
      )}
    </Container>
  );
};

export default ShopsPage;
