import styled from "@emotion/styled";


export const Container = styled.div`
  text-align: center;
`;

export const Title = styled.h2`
  font-size: 24px;
  color: #333;
`;

export const ShopList = styled.ul`
  list-style: none;
  padding: 0;
`;

export const ShopItem = styled.li`
  margin-bottom: 10px;
  cursor: pointer;
  color: #666;

  &:hover {
    color: #000;
    font-weight: bold;
  }
`;

export const SelectedShop = styled.p`
  margin-top: 20px;
`;

const ProductsTitle = styled.h3`
  margin-top: 20px;
`;
