import React, { useState } from "react";

const ShopsPage = ({ handleAddToCart }) => {
  const [selectedShop, setSelectedShop] = useState(null);

  const handleShopSelect = (shop) => {
    setSelectedShop(shop);
  };

  const renderProducts = () => {
    switch (selectedShop) {
      case "Mc Donny":
        return (
          <ul>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 1, name: "Burgers" }, selectedShop)
                }
              >
                Продукт 1
              </button>
            </li>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 2, name: "Продукт 2" }, selectedShop)
                }
              >
                Продукт 2
              </button>
            </li>
          </ul>
        );
      case "CFK":
        return (
          <ul>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 3, name: "Продукт 3" }, selectedShop)
                }
              >
                Продукт 3
              </button>
            </li>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 4, name: "Продукт 4" }, selectedShop)
                }
              >
                Продукт 4
              </button>
            </li>
          </ul>
        );
      case "Noa":
        return (
          <ul>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 5, name: "Продукт 5" }, selectedShop)
                }
              >
                Продукт 5
              </button>
            </li>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 6, name: "Продукт 6" }, selectedShop)
                }
              >
                Продукт 6
              </button>
            </li>
          </ul>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      <h2>Сторінка магазинів</h2>
      <ul>
        <li onClick={() => handleShopSelect("Mc Donny")}>Mc Donny</li>
        <li onClick={() => handleShopSelect("CFK")}>CFK</li>
        <li onClick={() => handleShopSelect("Noa")}>Noa</li>
      </ul>
      {selectedShop && (
        <div>
          <p>
            You have selected: <strong>{selectedShop}</strong>
          </p>
          <h3>Продукти</h3>
          {renderProducts()}
        </div>
      )}
    </div>
  );
};

export default ShopsPage;
