import React, { useState } from "react";

const ShopsPage = ({ handleAddToCart }) => {
  const [selectedShop, setSelectedShop] = useState(null);

  const handleShopSelect = (shop) => {
    setSelectedShop(shop);
  };

  return (
    <div>
      <h2>Сторінка магазинів</h2>
      <ul>
        <li onClick={() => handleShopSelect("")}>Mac</li>
        <li onClick={() => handleShopSelect("Магазин 2")}>CFC</li>
        <li onClick={() => handleShopSelect("Магазин 3")}>Noa</li>
      </ul>
      {selectedShop && (
        <div>
          <p>
            Ви вибрали магазин: <strong>{selectedShop}</strong>
          </p>
          <h3>Продукти</h3>
          <ul>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 1, name: "Продукт 1" }, selectedShop)
                }
              >
                Продукт 1
              </button>
            </li>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 2, name: "Продукт 2" }, selectedShop)
                }
              >
                Продукт 2
              </button>
            </li>
            <li>
              <button
                onClick={() =>
                  handleAddToCart({ id: 3, name: "Продукт 3" }, selectedShop)
                }
              >
                Продукт 3
              </button>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default ShopsPage;
