import React, { useState } from "react";
import { Routes, Route, NavLink } from "react-router-dom";
import ShopsPage from "./components/ShopsPage";
import CartPage from "./components/CartPage";

const App = () => {
  const [cartItems, setCartItems] = useState([]);
    const [selectedShop, setSelectedShop] = useState(null);

    const handleShopSelect = (shop) => {
      setSelectedShop(shop);
    };

const handleAddToCart = (product, shop) => {
  const updatedCartItems = { ...cartItems };

  if (shop in updatedCartItems) {
    const itemExists = updatedCartItems[shop].find(
      (item) => item.id === product.id
    );
    if (itemExists) {
      updatedCartItems[shop] = updatedCartItems[shop].map((item) =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      );
    } else {
      updatedCartItems[shop] = [
        ...updatedCartItems[shop],
        { ...product, quantity: 1 },
      ];
    }
  } else {
    updatedCartItems[shop] = [{ ...product, quantity: 1 }];
  }

  setCartItems(updatedCartItems);
};




  return (
    <div>
      <nav>
        <NavLink to="/" end>
          ShopsPage
        </NavLink>
        <NavLink to="/cart">Cart</NavLink>
      </nav>
      <Routes>
        <Route
          path="/"
          element={<ShopsPage handleAddToCart={handleAddToCart} />}
        />
        <Route
          path="/cart"
          element={
            <CartPage cartItems={cartItems} setCartItems={setCartItems} />
          }
        />
      </Routes>
    </div>
  );
};

export default App;
